package org.jetbrains.completion.full.line.python.formatters

import org.junit.jupiter.api.assertAll

class PythonFilesFormatterTest : PythonCodeFormatterTest() {
  override val formatter = PythonCodeFormatter()

  private val folder = "test-files/v2"

  fun `test data files`() {
    listOf(
      "data1.py",
      "data2.py",
      "data3.py",
      "__init__.py",
      "context_cutter.py",
      "empty_file_filter.py",
      "language_filter.py",
      "license_filter.py",
      "meta_info_composer.py",
      "abstract_processor.py",
      "autogenerated_regex_filter.py",
      "black_styler.py",
      "comment_remover.py",
      "empty_line_remover.py",
      "import_dropout.py",
      "indent_remover.py",
      "leading_whitespace_remover.py",
      "outliers_filter.py",
      "suffix_rollback.py",
      "tree_sitter_comment_remover.py",
    ).map { { testFile("$folder/$it") } }.let {
      assertAll("Testing formatting file", *it.toTypedArray())
    }
  }
}
